import React,{useState} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faClock,faCalendar} from '@fortawesome/free-solid-svg-icons'

 const Datetime = () => {
     
     var d=new Date();
     const[date,setdate]=useState(d.getDate());
     const[ month,setmonth]=useState(d.getMonth()+1);
     const[year,setyear]=useState(d.getFullYear());
     const[hours,sethours]=useState(d.getHours());
     const [tz,settz]=useState("");
     if(parseInt(hours)===0){
        sethours(12);
        settz("am");
    }
    else if(parseInt(hours)>=12){
         settz("pm");
         sethours(parseInt(hours)-12);
     }
     else{
         settz("am");
     }
    let minutes=d.getMinutes();

    return (
        <div className="datetime">
            <div className="date">
             <FontAwesomeIcon icon={faCalendar} size="2x" className="icon"/>
             <span>{date}/{month}/{year}</span>
            </div>
            <div className="time">
             
                 <FontAwesomeIcon icon={faClock} size="2x" className="icon"/>
                 <span>{hours}:{minutes} {tz}</span>
            
            </div>
            
        </div>
    )
}
export default Datetime
